const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

const App = () => {
  return (
    <>
      <Header />
      <Menu />
      <Footer />
    </>
  );
};

const Header = () => {
  return (
    <header className="w-full text-center text-gray-700 text-2xl bg-gray-50 p-2 mb-10">
      Navy Foods
    </header>
  );
};

const Pizza = () => {
  return (
    <div className="Pizza flex w-full min-h-screen">
      <img className=" w-60 h-auto"   src="pizzas/focaccia.jpg"></img>
      <h1  className=" "   >Focaccia </h1>
      <h2  className=" "   >Bread with italian olive oil and rosemary</h2>
      <h3  className=" "   >Price</h3>
    </div>
  );
};

const Menu = () => {
  return <>

  <Pizza />
  </>

  ;
};

const Footer = () => {
  return (
    <footer className="w-full text-center text-gray-700 text-sm bg-gray-100 p-2 mt-10">
      We're open
    </footer>
  );
};

export default App;
